<script>
export default {
  name: "bilibili-player-video-state-buff-icon",
  data(){
    return {
      animate_progress:0
    }
  },
  mounted() {
    let animate_fun = ()=>{
      if (this.animate_progress<=3){
        this.animate_progress+=0.01
      }else {
        clearInterval(animate)
        setTimeout(()=>{
          this.animate_progress=0
          animate = setInterval(animate_fun,1)
        },350)
      }
    }
    let animate = setInterval(animate_fun,1)
  }
}
